import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../../services/api";
import { Box, Card, CardContent, Typography, Button } from "@mui/material";

const Menu = () => {
  const { restaurantId } = useParams();
  const [foods, setFoods] = useState([]);

  useEffect(() => {
    const fetchFoods = async () => {
      try {
        const res = await api.get(`/foods/restaurant/${restaurantId}`);
        setFoods(res.data);
      } catch (err) {
        console.error("Failed to fetch foods", err);
      }
    };
    fetchFoods();
  }, [restaurantId]);

  const addToCart = (food) => {
    let cart = JSON.parse(localStorage.getItem("cart")) || [];
    cart.push(food);
    localStorage.setItem("cart", JSON.stringify(cart));
    alert("Added to cart!");
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" mb={3}>Menu</Typography>
      <Box display="flex" flexWrap="wrap" gap={2}>
        {foods.map((f) => (
          <Card key={f.id} sx={{ width: 250 }}>
            <CardContent>
              <Typography variant="h6">{f.name}</Typography>
              <Typography variant="body2">{f.description}</Typography>
              <Typography variant="body1">â‚¹{f.price}</Typography>
              {f.imageUrl && <img src={f.imageUrl} alt={f.name} width="100%" style={{ marginTop: "8px" }} />}
              <Button
                variant="contained"
                sx={{ mt: 2 }}
                onClick={() => addToCart(f)}
              >
                Add to Cart
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Menu;
