import React, { useEffect, useState } from "react";
import api from "../../services/api";
import { Box, Typography, Table, TableBody, TableCell, TableHead, TableRow } from "@mui/material";

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem("userId");

 useEffect(() => {
  const fetchOrders = async () => {
    try {
      const userId = localStorage.getItem("userId");
      const res = await api.get(`/user/orders?userId=${userId}`);
      setOrders(res.data);
    } catch (error) {
      console.error("Failed to fetch orders", error);
    }
  };
  fetchOrders();
}, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" mb={3}>My Orders</Typography>
      {orders.length === 0 ? (
        <Typography>No orders yet.</Typography>
      ) : (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Order ID</TableCell>
              <TableCell>Food Item</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map((o) => (
              <TableRow key={o.id}>
                <TableCell>{o.id}</TableCell>
                <TableCell>{o.menuItemName}</TableCell>
                <TableCell>{o.quantity}</TableCell>
                <TableCell>â‚¹{o.menuItemPrice}</TableCell>
                <TableCell>{o.orderStatus}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
    </Box>
  );
};

export default MyOrders;
