import React, { useEffect, useState } from "react";
import { Box, Typography, Card, CardContent, Grid } from "@mui/material";
import api from "../../utils/api";

const Feedbacks = () => {
  const [feedbacks, setFeedbacks] = useState([]);

  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("/api/hotels/feedbacks"); // endpoint to fetch hotel reviews
      setFeedbacks(res.data);
    } catch (error) {
      console.error("Failed to fetch feedbacks:", error);
    }
  };

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Feedbacks
      </Typography>
      {feedbacks.length === 0 ? (
        <Typography>No feedbacks yet.</Typography>
      ) : (
        <Grid container spacing={2}>
          {feedbacks.map((fb) => (
            <Grid item xs={12} sm={6} md={4} key={fb.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{fb.hotelName}</Typography>
                  <Typography variant="body2">Rating: {fb.rating}</Typography>
                  <Typography variant="body2">{fb.comment}</Typography>
                  <Typography variant="caption">By: {fb.userName}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
};

export default Feedbacks;
