import React, { useState } from "react";
import { Box, TextField, Button, Typography } from "@mui/material";
import api from "../../services/api";

const AddFood = ({ onFoodAdded }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const handleAddFood = async () => {
    const adminId = localStorage.getItem("userId"); // adminId = restaurantId

    if (!name || !price) {
      alert("Name and Price are required");
      return;
    }

    const newFood = {
      name,
      description,
      price: parseFloat(price),
      imageUrl,
    };

    try {
      const res = await api.post(`/hotel/foods?adminId=${adminId}`, newFood);
      if (res.status === 200 || res.status === 201) {
        alert("Food added successfully");
        onFoodAdded(res.data); // callback to refresh inventory
        setName("");
        setDescription("");
        setPrice("");
        setImageUrl("");
      }
    } catch (error) {
      console.error("Failed to add food:", error);
      alert("Error adding food");
    }
  };

  return (
    <Box padding={3} maxWidth={500} margin="auto">
      <Typography variant="h5" gutterBottom>
        Add New Food
      </Typography>
      <TextField
        fullWidth
        label="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Price"
        type="number"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        margin="normal"
      />
      <TextField
        fullWidth
        label="Image URL"
        value={imageUrl}
        onChange={(e) => setImageUrl(e.target.value)}
        margin="normal"
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleAddFood}
        sx={{ marginTop: 2 }}
      >
        Add Food
      </Button>
    </Box>
  );
};

export default AddFood;
