import api from "./api";

export const getAllRestaurants = () => api.get("/users/restaurants");
export const addRestaurant = (restaurant, hotelAdminId) =>
  api.post(`/admin/restaurants?hotelAdminId=${hotelAdminId}`, restaurant);
export const deleteRestaurant = (id) => api.delete(`/admin/restaurants/${id}`);
export const getRestaurantReviews = (restaurantId) =>
  api.get(`/users/reviews/restaurant/${restaurantId}`);
