import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

import Navbar from "./components/Navbar";
import Login from "./components/Login";
import Register from "./components/Register";
import Forget from "./components/Forget";
import UserHome from "./components/UserHome";
import AppAdmin from "./components/AdminHome";  // your AdminHome
import HotelAdmin from "./components/HAdminHome";  // your HotelAdminUser
// Add other imports as needed

const HideNavbarPaths = ["/", "/register", "/forgot-password"];

const App = () => {
  const location = useLocation();
  const hideNavbar = HideNavbarPaths.includes(location.pathname);

  return (
    <>
      {!hideNavbar && <Navbar />}
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<Forget />} />

        <Route path="/userhome" element={<UserHome />} />
        <Route path="/adminhome/*" element={<AppAdmin />} />
        <Route path="/hoteladmin/*" element={<HotelAdmin />} />
        {/* Add other routes */}
      </Routes>
    </>
  );
};

export default App;
