import React, { useState } from "react";
import { Box, TextField, Button } from "@mui/material";
import api from "../../utils/api";

const AddFood = ({ onFoodAdded }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const adminId = localStorage.getItem("userId"); // adminId = restaurantId

  const handleAddFood = async () => {
    if (!name || !price) {
      alert("Name and Price are required");
      return;
    }

    const newFood = {
      name,
      description,
      price: parseFloat(price),
      imageUrl,
    };

    try {
      const res = await api.post(`/hotel/foods?adminId=${adminId}`, newFood);

      if (res.status === 200 || res.status === 201) {
        alert("Food added successfully");
        onFoodAdded(res.data); // update inventory in parent
        setName("");
        setDescription("");
        setPrice("");
        setImageUrl("");
      }
    } catch (error) {
      console.error("Failed to add food:", error);
      alert("Error adding food");
    }
  };

  return (
    <Box display="flex" flexDirection="column" gap={2} mb={3}>
      <TextField label="Food Name" value={name} onChange={(e) => setName(e.target.value)} fullWidth />
      <TextField label="Description" value={description} onChange={(e) => setDescription(e.target.value)} fullWidth />
      <TextField label="Price" type="number" value={price} onChange={(e) => setPrice(e.target.value)} fullWidth />
      <TextField label="Image URL" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} fullWidth />
      <Button variant="contained" color="primary" onClick={handleAddFood}>
        Add Food
      </Button>
    </Box>
  );
};

export default AddFood;
