import React, { useEffect, useState } from "react";
import { Card, CardContent, Typography, Button, Grid } from "@mui/material";
import api from "../utils/api";

const OrderList = ({ refresh }) => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await api.get("/orders");
        setOrders(response.data);
      } catch (error) {
        console.error("Failed to fetch orders:", error);
      }
    };

    fetchOrders();
  }, [refresh]);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/orders/${id}`);
      setOrders(orders.filter((order) => order.id !== id));
    } catch (error) {
      console.error("Delete failed", error);
    }
  };

  if (orders.length === 0) return <Typography>No orders available.</Typography>;

  return (
    <Grid container spacing={2}>
      {orders.map((order) => (
        <Grid item xs={12} sm={6} md={4} key={order.id}>
          <Card>
            <CardContent>
              <Typography variant="h6">{order.menuItemName}</Typography>
              <Typography variant="body2">{order.restaurantName}</Typography>
              <Typography variant="body2">Status: {order.orderStatus}</Typography>
              <Button
                variant="outlined"
                color="error"
                sx={{ mt: 1 }}
                onClick={() => handleDelete(order.id)}
              >
                Delete
              </Button>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default OrderList;
