import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  Paper,
  Alert,
  Link,
  Stack
} from "@mui/material";

const Login = () => {
  const navigate = useNavigate();

  const [loginId, setLoginId] = useState(""); // username/email/phone
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // reset error

    try {
      const response = await axios.post("http://localhost:9000/api/users/login", {
        loginId,
        password
      });

      // Since backend returns plain token string:
      const token = response.data;
      localStorage.setItem("token", token);

      alert("✅ Login successful!");
      navigate("/dashboard"); 
    } catch (err) {
      console.error(err);
      setError("❌ Invalid credentials or server error");
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={5} sx={{ p: 7, mt: 30 }} align="center">
        <Typography variant="h4" align="center" gutterBottom>
          Login
        </Typography>

        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

        <Box component="form" onSubmit={handleLogin} display="flex" justifyContent="center"
      alignItems="center"
      minHeight="100vh" >
          <TextField
            fullWidth
            label="Username / Email / Phone"
            value={loginId}
            onChange={(e) => setLoginId(e.target.value)}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            type="password"
            label="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            margin="normal"
            required
          />

          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, py: 1.2 }}
          >
            Login
          </Button>

          <Stack direction="row" justifyContent="space-between" sx={{ mt: 2 }}>
            <Link
              component="button"
              variant="body2"
              onClick={() => navigate("/register")}
            >
              Register
            </Link>
            <Link
              component="button"
              variant="body2"
              onClick={() => navigate("/forgot-password")}
            >
              Forgot Password?
            </Link>
          </Stack>
        </Box>
      </Paper>
    </Container>
  );
};

export default Login;
