import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Button,
} from "@mui/material";

function UserHome() {
  const [foods, setFoods] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:9000/api/foods")
      .then((res) => setFoods(res.data))
      .catch((err) => console.error("Error fetching foods:", err));
  }, []);

  const handleAddToCart = (food) => {
    setCart((prevCart) => {
      const existing = prevCart.find((item) => item.id === food.id);
      if (existing) {
        return prevCart.map((item) =>
          item.id === food.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...food, quantity: 1 }];
      }
    });
  };

  const filteredFoods = foods.filter((food) =>
    food.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Welcome to Food Menu
      </Typography>

      <Grid container spacing={3}>
        {filteredFoods.map((food) => (
          <Grid item xs={12} sm={6} md={4} key={food.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={food.imageUrl}
                alt={food.name}
              />
              <CardContent>
                <Typography variant="h6" fontWeight="bold">
                  {food.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {food.description}
                </Typography>
                <Typography variant="h6" color="primary" mt={1}>
                  â‚¹{food.price}
                </Typography>
                <Button
                  variant="contained"
                  color="secondary"
                  fullWidth
                  sx={{ mt: 2 }}
                  onClick={() => handleAddToCart(food)}
                >
                  Add to Cart
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Cart Summary */}
      {cart.length > 0 && (
        <Box mt={4} p={2} border="1px solid #ccc" borderRadius={2}>
          <Typography variant="h5">ðŸ›’ Your Cart</Typography>
          {cart.map((item) => (
            <Typography key={item.id}>
              {item.name} x {item.quantity} = â‚¹{item.price * item.quantity}
            </Typography>
          ))}
          <Typography variant="h6" mt={2}>
            Total: â‚¹
            {cart.reduce((sum, item) => sum + item.price * item.quantity, 0)}
          </Typography>
          <Button
            variant="contained"
            color="primary"
            sx={{ mt: 2 }}
            onClick={() => {
              axios
                .post("http://localhost:9000/api/orders", { items: cart })
                .then((res) => {
                  alert("Order placed! Bill ID: " + res.data.id);
                  setCart([]);
                })
                .catch((err) => console.error("Error placing order:", err));
            }}
          >
            Place Order
          </Button>
        </Box>
      )}

      <Box
        component="footer"
        sx={{
          bgcolor: "primary.main",
          color: "white",
          textAlign: "center",
          py: 2,
          mt: 4,
        }}
      >
        <Typography variant="body2">
          Â© {new Date().getFullYear()} Food Ordering App. All Rights Reserved.
        </Typography>
      </Box>
    </Box>
  );
}

export default UserHome;
