import React, { useEffect, useState } from "react";
import { Box, Typography, Table, TableBody, TableCell, TableHead, TableRow, Button, TextField } from "@mui/material";
import { getAllRestaurants, updateRestaurant, deleteRestaurant } from "../../services/restaurantService";

const ManageRestaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [editName, setEditName] = useState({});

  const fetchRestaurants = async () => {
    const res = await getAllRestaurants();
    setRestaurants(res.data);
  };

  const handleDelete = async (id) => {
    await deleteRestaurant(id);
    fetchRestaurants();
  };

  const handleEditChange = (id, value) => {
    setEditName({ ...editName, [id]: value });
  };

  const handleUpdate = async (id) => {
    await updateRestaurant(id, { name: editName[id] });
    fetchRestaurants();
  };

  useEffect(() => {
    fetchRestaurants();
  }, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Manage Restaurants
      </Typography>
      {restaurants.length === 0 ? (
        <Typography>No restaurants found.</Typography>
      ) : (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Address</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {restaurants.map((rest) => (
              <TableRow key={rest.id}>
                <TableCell>
                  <TextField
                    value={editName[rest.id] ?? rest.name}
                    onChange={(e) => handleEditChange(rest.id, e.target.value)}
                  />
                </TableCell>
                <TableCell>{rest.address}</TableCell>
                <TableCell>
                  <Button variant="contained" color="primary" onClick={() => handleUpdate(rest.id)} sx={{ mr: 1 }}>
                    Update
                  </Button>
                  <Button variant="contained" color="error" onClick={() => handleDelete(rest.id)}>
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
    </Box>
  );
};

export default ManageRestaurants;
