import React, { useState } from "react";
import { addRestaurant } from "../../services/adminapi";
import { TextField, Button } from "@mui/material";

const AddRestaurant = () => {
  const [restaurant, setRestaurant] = useState({ name: "", location: "" });
  const [hotelAdminId, setHotelAdminId] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    addRestaurant(restaurant, hotelAdminId).then(() => {
      setRestaurant({ name: "", location: "" });
      setHotelAdminId("");
    });
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Add Restaurant</h2>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Name"
          value={restaurant.name}
          onChange={(e) => setRestaurant({ ...restaurant, name: e.target.value })}
          fullWidth
          className="mb-3"
        />
        <TextField
          label="Location"
          value={restaurant.location}
          onChange={(e) => setRestaurant({ ...restaurant, location: e.target.value })}
          fullWidth
          className="mb-3"
        />
        <TextField
          label="Hotel Admin ID"
          value={hotelAdminId}
          onChange={(e) => setHotelAdminId(e.target.value)}
          fullWidth
          className="mb-3"
        />
        <Button type="submit" variant="contained" color="primary">Add</Button>
      </form>
    </div>
  );
};

export default AddRestaurant;
