// src/pages/ReviewPage.jsx
import React, { useState } from "react";
import { TextField, Button, Typography } from "@mui/material";
import axios from "axios";

const ReviewPage = () => {
  const [review, setReview] = useState("");
  const [targetId, setTargetId] = useState("");
  const [type, setType] = useState("restaurant"); // restaurant | dish

  const handleSubmit = async () => {
    try {
      const url =
        type === "restaurant"
          ? `http://localhost:8080/reviews/restaurant/${targetId}`
          : `http://localhost:8080/reviews/dish/${targetId}`;

      await axios.post(url, {
        userId: 1, // replace with logged-in user
        review,
      });

      setReview("");
      alert("Review submitted!");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-4">
      <Typography variant="h4">Post Review</Typography>
      <TextField
        label="Target ID (Restaurant/Dish)"
        value={targetId}
        onChange={(e) => setTargetId(e.target.value)}
        fullWidth
        className="mb-2"
      />
      <TextField
        label="Review"
        value={review}
        onChange={(e) => setReview(e.target.value)}
        fullWidth
        multiline
        rows={3}
        className="mb-2"
      />
      <Button variant="contained" onClick={handleSubmit}>Submit</Button>
    </div>
  );
};

export default ReviewPage;
