import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Card, CardContent } from "@mui/material";
import { getAllFoods, addFood, deleteFood } from "../services/foodService";
import { getOrdersByRestaurant, getProfitByRestaurant } from "../services/orderService";
import OrderList from "../components/OrderList";

function HotelAdmin() {
  const username = localStorage.getItem("username") || "HotelAdmin";

  const [foods, setFoods] = useState([]);
  const [ordersRefresh, setOrdersRefresh] = useState(false);
  const [profit, setProfit] = useState(0);
  const [newFood, setNewFood] = useState({ name: "", price: 0, description: "" });

  useEffect(() => {
    fetchFoods();
    calculateProfit();
  }, []);

  const fetchFoods = async () => {
    const res = await getAllFoods();
    setFoods(res.data);
  };

  const calculateProfit = async () => {
    const res = await getProfitByRestaurant(1); // replace with actual restaurantId
    setProfit(res.data);
  };

  const handleAddFood = async () => {
    await addFood(newFood, 1);
    setNewFood({ name: "", price: 0, description: "" });
    fetchFoods();
  };

  const handleDeleteFood = async (id) => {
    await deleteFood(id);
    fetchFoods();
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Admin Dashboard
      </Typography>
      <Typography mb={2}>Manage inventory, orders, and profit here.</Typography>
      <Typography variant="body1">Hello! {username}</Typography>

      {/* Add Food Section */}
      <Box mt={3} display="flex" gap={2}>
        <input
          type="text"
          placeholder="Food Name"
          value={newFood.name}
          onChange={(e) => setNewFood({ ...newFood, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={newFood.price}
          onChange={(e) => setNewFood({ ...newFood, price: parseFloat(e.target.value) })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newFood.description}
          onChange={(e) => setNewFood({ ...newFood, description: e.target.value })}
        />
        <Button onClick={handleAddFood} variant="contained">
          Add Food
        </Button>
      </Box>

      {/* Foods List */}
      <Typography variant="h5" mt={3}>
        Inventory
      </Typography>
      <Grid container spacing={2} mt={1}>
        {foods.map((food) => (
          <Grid item xs={12} sm={6} md={4} key={food.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{food.name}</Typography>
                <Typography variant="body2">{food.description}</Typography>
                <Typography variant="body1">₹{food.price}</Typography>
                <Button
                  color="error"
                  variant="outlined"
                  sx={{ mt: 1 }}
                  onClick={() => handleDeleteFood(food.id)}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Orders List */}
      <Box mt={4}>
        <Typography variant="h5">Orders</Typography>
        <OrderList refresh={ordersRefresh} />
        <Button
          variant="contained"
          sx={{ mt: 2 }}
          onClick={() => setOrdersRefresh(!ordersRefresh)}
        >
          Refresh Orders
        </Button>
      </Box>

      {/* Profit */}
      <Box mt={4}>
        <Typography variant="h5">Total Profit: ₹{profit}</Typography>
      </Box>
    </Box>
  );
}

export default HotelAdmin;
