import React, { useState } from "react";
import { addRestaurant } from "../../services/adminapi";
import { TextField, Button, Paper, Typography } from "@mui/material";

const AddRestaurant = () => {
  const [restaurant, setRestaurant] = useState({ name: "", location: "" });
  const [hotelAdminId, setHotelAdminId] = useState("");

  const handleSubmit = (e) => {
  e.preventDefault();

  const payload = {
    ...restaurant
  };

  try{
    addRestaurant(payload,hotelAdminId).then(() => {
      setRestaurant({ name: "", location: "" });
      setHotelAdminId("");
    });
  }
  catch (error) {
    console.error("Failed to add restaurant:", error);
    alert("Failed to add restaurant. Please check the console for details.");
  }
};


  return (
    <Paper elevation={3} sx={{ p: 4, maxWidth: 500, mx: "auto", mt: 5 }}>
      <Typography variant="h5" color="primary" gutterBottom fontWeight="bold">
        Add Restaurant
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Name"
          value={restaurant.name}
          onChange={(e) => setRestaurant({ ...restaurant, name: e.target.value })}
          fullWidth
          sx={{ mb: 2 }}
          color="primary"
        />
        <TextField
          label="Location"
          value={restaurant.location}
          onChange={(e) =>
            setRestaurant({ ...restaurant, location: e.target.value })
          }
          fullWidth
          sx={{ mb: 2 }}
          color="primary"
        />
        <TextField
          label="Hotel Admin ID"
          value={hotelAdminId}
          onChange={(e) => setHotelAdminId(e.target.value)}
          fullWidth
          sx={{ mb: 3 }}
          color="primary"
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          sx={{ py: 1.2, fontWeight: "bold" }}
        >
          Add Restaurant
        </Button>
      </form>
    </Paper>
  );
};

export default AddRestaurant;
