import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import api from "../../utils/api";

const AddFood = () => {
  const [food, setFood] = useState({ name: "", price: "", description: "", imageUrl: "" });
  const userId = localStorage.getItem("userId"); // hotel admin's user ID

  const handleChange = (e) => {
    setFood({ ...food, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId) {
      alert("User ID not found. Cannot add food.");
      return;
    }

    try {
      const payload = { ...food, price: parseFloat(food.price) };
     await api.post(`/api/hotel/foods?restaurantId=${userId}`, payload);

      alert("Food added successfully!");
      setFood({ name: "", price: "", description: "", imageUrl: "" });
    } catch (error) {
      console.error("Failed to add food:", error);
      alert("Failed to add food. Please check the console for details.");
    }
  };

  return (
    <Box padding={3} maxWidth={500} mx="auto">
      <Typography variant="h4" gutterBottom>
        Add New Food
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Name"
          name="name"
          value={food.name}
          onChange={handleChange}
          fullWidth
          margin="normal"
          required
        />
        <TextField
          label="Price"
          name="price"
          value={food.price}
          onChange={handleChange}
          type="number"
          fullWidth
          margin="normal"
          required
        />
        <TextField
          label="Description"
          name="description"
          value={food.description}
          onChange={handleChange}
          fullWidth
          margin="normal"
          required
        />
        <TextField
          label="Image URL"
          name="imageUrl"
          value={food.imageUrl}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit" sx={{ mt: 2 }}>
          Add Food
        </Button>
      </form>
    </Box>
  );
};

export default AddFood;
