import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

// Components
import Navbar from "./components/Navbar";
import Login from "./components/Login";
import Register from "./components/Register";
import Forget from "./components/Forget";
import UserHome from "./components/UserHome";
import AppAdmin from "./components/AdminHome";      // App Admin dashboard
import HotelAdminUser from "./components/HAdminUser"; // Hotel Admin main page

// Hotel Admin Subpages
import HotelAdminHome from "./components/HotelAdminHome";      // Profit / Dashboard
import HotelAdminOrders from "./components/HotelAdminOrders";  // Orders page
import HotelAdminInventory from "./components/HotelAdminInventory"; // Inventory page
import AddFood from "./components/AddFood";                    // Add Food page

// Paths where Navbar should be hidden
const HideNavbarPaths = ["/", "/register", "/forgot-password"];

const AppWrapper = () => {
  const location = useLocation();
  const hideNavbar = HideNavbarPaths.includes(location.pathname);

  return (
    <>
      {/* Show Navbar only on certain pages */}
      {!hideNavbar && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<Forget />} />

        {/* User Routes */}
        <Route path="/userhome" element={<UserHome />} />

        {/* App Admin Routes */}
        <Route path="/adminhome/*" element={<AppAdmin />} />

        {/* Hotel Admin Routes */}
        <Route path="/hoteladmin" element={<HotelAdmin />} />
        <Route path="/hoteladmin/orders" element={<HotelAdminOrders />} />
        <Route path="/hoteladmin/inventory" element={<HotelAdminInventory />} />
        <Route path="/add-food" element={<AddFood />} />
      </Routes>
    </>
  );
};

const App = () => (
  <Router>
    <AppWrapper />
  </Router>
);

export default App;
