import React, { useEffect, useState } from "react";
import api, { getRestaurants } from "../../services/api";
import { Box, Card, CardContent, Button, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";

const UserHome = () => {
  const [restaurants, setRestaurants] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getRestaurants()
      .then((res) => setRestaurants(res.data))
      .catch((err) => console.error("Failed to fetch restaurants", err));
  }, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" mb={3}>Restaurants</Typography>
      <Box display="flex" flexWrap="wrap" gap={2}>
        {restaurants.map((r) => (
          <Card key={r.id} sx={{ width: 250 }}>
            <CardContent>
              <Typography variant="h6">{r.name}</Typography>
              <Typography variant="body2">{r.address}</Typography>
              {r.imageUrl && <img src={r.imageUrl} alt={r.name} width="100%" style={{ marginTop: "8px" }} />}
              <Button
                variant="contained"
                sx={{ mt: 2 }}
                onClick={() => navigate(`/userhome/menu/${r.id}`)}
              >
                View Menu
              </Button>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default UserHome;
