import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Card, CardContent } from "@mui/material";
import { getAllFoods, addFood, updateFood, deleteFood } from "../services/foodService";
import { getOrdersByRestaurant, getProfitByRestaurant } from "../services/orderService";

function HotelAdmin() {
  const username = localStorage.getItem("username") || "HotelAdmin";
  const [foods, setFoods] = useState([]);
  const [orders, setOrders] = useState([]);
  const [profit, setProfit] = useState(0);
  const [newFood, setNewFood] = useState({ name: "", price: 0, description: "" });

  useEffect(() => {
    fetchFoods();
    fetchOrders();
    calculateProfit();
  }, []);

  const fetchFoods = async () => {
    const res = await getAllFoods();
    setFoods(res.data);
  };

  const fetchOrders = async () => {
    const res = await getOrdersByRestaurant(1); // replace with actual restaurantId
    setOrders(res.data);
  };

  const calculateProfit = async () => {
    const res = await getProfitByRestaurant(1);
    setProfit(res.data);
  };

  const handleAddFood = async () => {
    await addFood(newFood, 1);
    setNewFood({ name: "", price: 0, description: "" });
    fetchFoods();
  };

  const handleDeleteFood = async (id) => {
    await deleteFood(id);
    fetchFoods();
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Admin Dashboard
      </Typography>
      <Typography mb={2}>Manage inventory and orders here.</Typography>
      <Typography variant="body1">Hello! {username}</Typography>

      <Box mt={3}>
        <input
          type="text"
          placeholder="Food Name"
          value={newFood.name}
          onChange={(e) => setNewFood({ ...newFood, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={newFood.price}
          onChange={(e) => setNewFood({ ...newFood, price: parseFloat(e.target.value) })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newFood.description}
          onChange={(e) => setNewFood({ ...newFood, description: e.target.value })}
        />
        <Button onClick={handleAddFood} variant="contained" sx={{ ml: 1 }}>
          Add Food
        </Button>
      </Box>

      <Grid container spacing={2} mt={3}>
        {foods.map((food) => (
          <Grid item xs={12} sm={6} md={4} key={food.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{food.name}</Typography>
                <Typography variant="body2">{food.description}</Typography>
                <Typography variant="body1">₹{food.price}</Typography>
                <Button
                  color="error"
                  variant="outlined"
                  onClick={() => handleDeleteFood(food.id)}
                  sx={{ mt: 1 }}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Box mt={4}>
        <Typography variant="h5">Orders:</Typography>
        {orders.map((order) => (
          <Card key={order.id} sx={{ mt: 1 }}>
            <CardContent>
              <Typography>Order #{order.id}</Typography>
              <Typography>Menu: {order.menuItemName}</Typography>
              <Typography>Quantity: {order.quantity}</Typography>
              <Typography>Status: {order.orderStatus}</Typography>
            </CardContent>
          </Card>
        ))}
        <Typography variant="h6" mt={2}>
          Total Profit: ₹{profit}
        </Typography>
      </Box>
    </Box>
  );
}

export default HotelAdmin;
