import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Card, CardContent } from "@mui/material";
import { getAllRestaurants, addRestaurant, deleteRestaurant } from "../../services/restaurantService";

function AppAdmin() {
  const username = localStorage.getItem("username") || "Admin";
  const [restaurants, setRestaurants] = useState([]);
  const [newRestaurantName, setNewRestaurantName] = useState("");

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    const res = await getAllRestaurants();
    setRestaurants(res.data);
  };

  const handleAdd = async () => {
    if (!newRestaurantName) return;
    await addRestaurant({ name: newRestaurantName }, 1); // hotelAdminId can be dynamic
    setNewRestaurantName("");
    fetchRestaurants();
  };

  const handleDelete = async (id) => {
    await deleteRestaurant(id);
    fetchRestaurants();
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        App Admin Dashboard
      </Typography>
      <Typography mb={2}>Manage restaurants and view feedback here.</Typography>
      <Typography variant="body1">Hello, {username}</Typography>

      <Box mt={3}>
        <input
          type="text"
          placeholder="New Restaurant Name"
          value={newRestaurantName}
          onChange={(e) => setNewRestaurantName(e.target.value)}
        />
        <Button onClick={handleAdd} variant="contained" sx={{ ml: 1 }}>
          Add Restaurant
        </Button>
      </Box>

      <Grid container spacing={2} mt={3}>
        {restaurants.map((rest) => (
          <Grid item xs={12} sm={6} md={4} key={rest.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{rest.name}</Typography>
                <Typography variant="body2">{rest.address}</Typography>
                <Button
                  color="error"
                  variant="outlined"
                  onClick={() => handleDelete(rest.id)}
                  sx={{ mt: 1 }}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default AppAdmin;
