import React, { useEffect, useState } from "react";
import { Box, Typography } from "@mui/material";
import api from "../../services/api";

const HotelAdminHome = () => {
  const [orders, setOrders] = useState([]);
  const [profit, setProfit] = useState(0);

  // Replace this with the logged-in Hotel Admin ID
  const hotelAdminId = 1; // Example, fetch dynamically from auth/context

  useEffect(() => {
    const fetchOrdersAndProfit = async () => {
      try {
        // Fetch all orders for this hotel admin
        const ordersRes = await api.get(`/hotel/orders?adminId=${hotelAdminId}`);
        setOrders(ordersRes.data);

        // Fetch total profit from backend
        const profitRes = await api.get(`/hotel/profit?adminId=${hotelAdminId}`);
        setProfit(profitRes.data);
      } catch (error) {
        console.error("Error fetching orders or profit:", error);
      }
    };

    fetchOrdersAndProfit();
  }, [hotelAdminId]);

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Admin Dashboard
      </Typography>
      <Typography variant="h6" mb={2}>
        Total Profit: â‚¹{profit.toFixed(2)}
      </Typography>
      <Typography variant="body1">
        Total Orders: {orders.length}
      </Typography>
    </Box>
  );
};

export default HotelAdminHome;
