import React, { useEffect, useState } from "react";
import { Box, Typography } from "@mui/material";
import api from "../../utils/api";
import FoodCard from "../FoodCard";
import AddFood from "./AddFood";

const HotelAdminInventory = () => {
  const [foods, setFoods] = useState([]);

  const adminId = localStorage.getItem("userId"); // adminId = restaurantId

  const fetchFoods = async () => {
    try {
      const res = await api.get(`/hotel/foods?adminId=${adminId}`);
      setFoods(res.data);
    } catch (error) {
      console.error("Failed to fetch foods:", error);
    }
  };

  useEffect(() => {
    fetchFoods();
  }, []);

  const handleDelete = async (foodId) => {
    try {
      await api.delete(`/hotel/foods/${foodId}`);
      setFoods((prev) => prev.filter((f) => f.id !== foodId));
    } catch (error) {
      console.error("Delete failed:", error);
    }
  };

  const handleAddFood = (newFood) => {
    setFoods((prev) => [...prev, newFood]);
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Inventory
      </Typography>

      <AddFood onFoodAdded={handleAddFood} />

      {foods.length === 0 ? (
        <Typography mt={2}>No foods yet.</Typography>
      ) : (
        <Box display="flex" flexWrap="wrap" gap={2} mt={2}>
          {foods.map((food) => (
            <FoodCard key={food.id} food={food} onDelete={() => handleDelete(food.id)} />
          ))}
        </Box>
      )}
    </Box>
  );
};

export default HotelAdminInventory;
