import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import Navbar from "./components/Navbar"; // Your shared navbar
import Login from "./components/Login";
import Register from "./components/Register";
import Forget from "./components/Forget";
import UserHome from "./components/UserHome";
import AppAdmin from "./components/Admin";
import HotelAdmin from "./components/HAdmin";
import AddRestaurant from "./components/Restaurant";

const App = () => {
  return (
    <Router>
      {/* Navbar shown on all pages except login, register, forgot maybe */}
      {window.location.pathname !== "/" &&
       window.location.pathname !== "/register" &&
       window.location.pathname !== "/forgot-password" && <Navbar />}

      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<Forget />} />

        {/* Protected / logged-in user routes */}
        <Route path="/userhome" element={<UserHome />} />
        <Route path="/adminhome/*" element={<AppAdmin />} />
        <Route path="/hoteladmin/*" element={<HotelAdmin />} />
        <Route path="/adminhome/addrestaurant" element={<AddRestaurant />} />
      </Routes>
    </Router>
  );
};

export default App;
