import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Nav, Container, Card, Button, Row, Col } from "react-bootstrap";

const foods = [
  {
    id: 1,
    name: "Margherita Pizza",
    description: "Classic pizza topped with fresh tomatoes, mozzarella & basil.",
    price: 299,
    image: "https://source.unsplash.com/400x300/?pizza",
    time: "30 mins"
  },
  {
    id: 2,
    name: "Veg Burger",
    description: "Juicy vegetable patty with lettuce, cheese, and mayo.",
    price: 199,
    image: "https://source.unsplash.com/400x300/?burger",
    time: "20 mins"
  },
  {
    id: 3,
    name: "Pasta Alfredo",
    description: "Creamy Alfredo pasta with parmesan and herbs.",
    price: 249,
    image: "https://source.unsplash.com/400x300/?pasta",
    time: "25 mins"
  }
];

function Home() {
  const handleOrder = (foodName) => {
    alert(`Your order for ${foodName} has been placed!`);
  };

  return (
    <div>
      {/* Navbar */}
      <Navbar bg="dark" variant="dark" expand="lg">
        <Container>
          <Navbar.Brand href="#home">Foodie App</Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarNav" />
          <Navbar.Collapse id="navbarNav">
            <Nav className="ms-auto">
              <Nav.Link href="#menu">Menu</Nav.Link>
              <Nav.Link href="#orders">Orders</Nav.Link>
              <Nav.Link href="#cart">Cart</Nav.Link>
              <Nav.Link href="#profile">Profile</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Food Items */}
      <Container className="mt-4">
        <h2 className="mb-4">Our Delicious Menu</h2>
        <Row>
          {foods.map((food) => (
            <Col key={food.id} md={4} className="mb-4">
              <Card>
                <Card.Img variant="top" src={food.image} />
                <Card.Body>
                  <Card.Title>{food.name}</Card.Title>
                  <Card.Text>{food.description}</Card.Text>
                  <h5>₹{food.price}</h5>
                  <p>⏱ Estimated Time: {food.time}</p>
                  <Button
                    variant="success"
                    onClick={() => handleOrder(food.name)}
                  >
                    Ready to Get Order
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
}

export default Home;
