import api from "./api";

// Get all foods (for admin view or all restaurants)
export const getAllFoods = () => api.get("/foods");

// Get foods by specific restaurant
export const getFoodsByRestaurant = (restaurantId) =>
  api.get(`/foods/restaurant/${restaurantId}`);

// Add new food for a restaurant
export const addFood = (food, restaurantId) =>
  api.post(`/foods/restaurant/${restaurantId}`, food);

// Update existing food
export const updateFood = (food) =>
  api.put(`/foods/${food.id}`, food);

// Delete food by ID
export const deleteFood = (foodId) =>
  api.delete(`/foods/${foodId}`);
