import React from "react";
import { AppBar, Toolbar, Typography, Button, Box } from "@mui/material";
import FastfoodIcon from "@mui/icons-material/Fastfood";
import { useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const role = localStorage.getItem("role");
  const username = localStorage.getItem("username");

  const handleLogout = () => {
    alert("You have been logged out successfully!");
    localStorage.clear();
    navigate("/");
  };

  // Helper to navigate to home based on role
  const goHome = () => {
    if (role === "APP_ADMIN") navigate("/adminhome");
    else if (role === "HOTEL_ADMIN") navigate("/hoteladmin");
    else navigate("/userhome");
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <FastfoodIcon sx={{ mr: 2 }} />
        <Typography
          variant="h6"
          sx={{ flexGrow: 1, cursor: "pointer" }}
          onClick={goHome}
        >
          Food Ordering App
        </Typography>

        <Button color="inherit" onClick={goHome}>
          Home
        </Button>

        {/* Role-based navigation */}
        {role === "APP_ADMIN" && (
          <>
            <Button color="inherit" onClick={() => navigate("/adminhome")}>
              Manage Restaurants
            </Button>
            <Button color="inherit" onClick={() => navigate("/feedback")}>
              Feedbacks
            </Button>
            <Button color="inherit" onClick={() => navigate("/add-restaurant")}>
              Add Restaurant
            </Button>
          </>
        )}

        {role === "HOTEL_ADMIN" && (
          <>
            <Button color="inherit" onClick={() => navigate("/hoteladmin/orders")}>
              Orders
            </Button>
            <Button color="inherit" onClick={() => navigate("/hoteladmin/inventory")}>
              Inventory
            </Button>
            <Button color="inherit" onClick={() => navigate("/add-food")}>
              Add Food
            </Button>
          </>
        )}

        {role === "USER" && (
          <>
            <Button color="inherit" onClick={() => navigate("/userhome")}>
              Menu
            </Button>
            <Button color="inherit" onClick={() => navigate("/orders")}>
              My Orders
            </Button>
            <Button color="inherit" onClick={() => navigate("/reviews")}>
              Reviews
            </Button>
          </>
        )}

        <Box sx={{ ml: 2, display: "flex", alignItems: "center" }}>
          <Typography variant="body1" component="span" sx={{ mr: 2 }}>
            Hi, {username}
          </Typography>
          <Button color="inherit" onClick={handleLogout}>
            Logout
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
