import React, { useEffect, useState } from "react";
import { getAllRestaurants, getRestaurantReviews } from "../../services/adminapi";
import { Card, CardContent, Button } from "@mui/material";

const Feedbacks = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [reviews, setReviews] = useState({});

  useEffect(() => {
    getAllRestaurants().then((res) => setRestaurants(res.data));
  }, []);

  const handleViewReviews = (id) => {
    getRestaurantReviews(id).then((res) => {
      setReviews((prev) => ({ ...prev, [id]: res.data }));
    });
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Restaurant Feedbacks</h2>
      {restaurants.map((r) => (
        <Card key={r.id} className="mb-3 shadow-md">
          <CardContent>
            <p><strong>{r.name}</strong> - {r.location}</p>
            <Button onClick={() => handleViewReviews(r.id)} variant="outlined">View Reviews</Button>
            {reviews[r.id] && (
              <div className="mt-3">
                {reviews[r.id].length > 0 ? (
                  reviews[r.id].map((rev) => (
                    <p key={rev.id}>‚≠ê {rev.rating} - {rev.comment}</p>
                  ))
                ) : (
                  <p>No reviews yet</p>
                )}
              </div>
            )}
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default Feedbacks;
