import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
} from "@mui/material";

import { 
  getFoodsByRestaurant, 
  addFood, 
  updateFood, 
  deleteFood 
} from "../services/foodService";

function HotelAdminUser() {
  const username = localStorage.getItem("username") || "HotelAdmin";
  const restaurantId = localStorage.getItem("restaurantId"); // store restaurantId during login

  const [foods, setFoods] = useState([]);
  const [open, setOpen] = useState(false);
  const [currentFood, setCurrentFood] = useState({ name: "", price: "", description: "", imageUrl: "" });

  const fetchFoods = async () => {
    try {
      const res = await getFoodsByRestaurant(restaurantId);
      setFoods(res.data);
    } catch (err) {
      console.error("Error fetching foods:", err);
    }
  };

  useEffect(() => {
    fetchFoods();
  }, []);

  const handleOpen = (food = { name: "", price: "", description: "", imageUrl: "" }) => {
    setCurrentFood(food);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setCurrentFood({ name: "", price: "", description: "", imageUrl: "" });
  };

  const handleSave = async () => {
    try {
      if (currentFood.id) {
        await updateFood(currentFood);
      } else {
        await addFood(currentFood, restaurantId);
      }
      fetchFoods();
      handleClose();
    } catch (err) {
      console.error("Save failed:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteFood(id);
      fetchFoods();
    } catch (err) {
      console.error("Delete failed:", err);
    }
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Admin Dashboard
      </Typography>
      <Typography mb={2}>Manage inventory and orders here.</Typography>
      <Typography variant="body1">Hello! {username}</Typography>

      <Button variant="contained" color="primary" onClick={() => handleOpen()} sx={{ my: 2 }}>
        Add New Food
      </Button>

      <Grid container spacing={3}>
        {foods.map((food) => (
          <Grid item xs={12} sm={6} md={4} key={food.id}>
            <Card>
              <CardMedia component="img" height="200" image={food.imageUrl} alt={food.name} />
              <CardContent>
                <Typography variant="h6" fontWeight="bold">
                  {food.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {food.description}
                </Typography>
                <Typography variant="h6" color="primary" mt={1}>
                  â‚¹{food.price}
                </Typography>
                <Button size="small" onClick={() => handleOpen(food)}>Edit</Button>
                <Button size="small" color="error" onClick={() => handleDelete(food.id)}>Delete</Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Add/Edit Food Dialog */}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{currentFood.id ? "Edit Food" : "Add Food"}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Name"
            fullWidth
            value={currentFood.name}
            onChange={(e) => setCurrentFood({ ...currentFood, name: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Price"
            type="number"
            fullWidth
            value={currentFood.price}
            onChange={(e) => setCurrentFood({ ...currentFood, price: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Description"
            fullWidth
            value={currentFood.description}
            onChange={(e) => setCurrentFood({ ...currentFood, description: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Image URL"
            fullWidth
            value={currentFood.imageUrl}
            onChange={(e) => setCurrentFood({ ...currentFood, imageUrl: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleSave} color="primary">{currentFood.id ? "Update" : "Add"}</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default HotelAdminUser;
