import React, { useEffect, useState } from "react";
import api from "../utils/api";
import { Box, Typography, Table, TableBody, TableCell, TableHead, TableRow, Button } from "@mui/material";

const HotelAdminInventory = () => {
  const [foods, setFoods] = useState([]);

  const fetchFoods = async () => {
    try {
      const res = await api.get("/hotel/foods"); // backend: fetch foods by hotel
      setFoods(res.data);
    } catch (error) {
      console.error("Failed to fetch foods:", error);
    }
  };

  const handleDelete = async (foodId) => {
    try {
      await api.delete(`/hotel/foods/${foodId}`);
      setFoods(foods.filter((f) => f.id !== foodId));
    } catch (error) {
      console.error("Delete failed:", error);
    }
  };

  useEffect(() => {
    fetchFoods();
  }, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Inventory
      </Typography>
      {foods.length === 0 ? (
        <Typography>No foods yet.</Typography>
      ) : (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Description</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {foods.map((food) => (
              <TableRow key={food.id}>
                <TableCell>{food.name}</TableCell>
                <TableCell>â‚¹{food.price}</TableCell>
                <TableCell>{food.description}</TableCell>
                <TableCell>
                  <Button variant="contained" color="error" onClick={() => handleDelete(food.id)}>
                    Delete
                  </Button>
                  {/* Add edit button if needed */}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
    </Box>
  );
};

export default HotelAdminInventory;
