import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

// Components
import Navbar from "./components/Navbar";
import Login from "./components/Login";
import Register from "./components/Register";
import Forget from "./components/Forget";

// User Components
import UserHome from "./components/User/UserHome";

// App Admin Components
import AdminHome from "./components/AppAdmin/AdminHome";       // Lists all users
import ManageRestaurants from "./components/AppAdmin/ManageRestaurants"; // Update/Delete restaurants
import Feedbacks from "./components/AppAdmin/Feedback";             // Hotel reviews
import AddRestaurant from "./components/AppAdmin/AddRestaurant";     // Add new restaurant

// Hotel Admin Components
import HotelAdminHome from "./components/HotelAdmin/HAdminHome";      // Hotel Admin main page
import HotelAdminOrders from "./components/HotelAdmin/HotelAdminOrders";  // Orders page
import HotelAdminInventory from "./components/HotelAdmin/HotelAdminInventory"; // Inventory page
import AddFood from "./components/HotelAdmin/AddFood";               // Add Food page

// Paths where Navbar should be hidden
const HideNavbarPaths = ["/", "/register", "/forgot-password"];

const AppWrapper = () => {
  const location = useLocation();
  const hideNavbar = HideNavbarPaths.includes(location.pathname);

  return (
    <>
      {/* Show Navbar only on certain pages */}
      {!hideNavbar && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgot-password" element={<Forget />} />

        {/* User Routes */}
        <Route path="/userhome" element={<UserHome />} />

        {/* App Admin Routes */}
        <Route path="/adminhome" element={<AdminHome />} />
        <Route path="/adminhome/manage-restaurants" element={<ManageRestaurants />} />
        <Route path="/adminhome/feedbacks" element={<Feedbacks />} />
        <Route path="/adminhome/add-restaurant" element={<AddRestaurant />} />

        {/* Hotel Admin Routes */}
        <Route path="/hoteladmin" element={<HotelAdminHome />} />
        <Route path="/hoteladmin/orders" element={<HotelAdminOrders />} />
        <Route path="/hoteladmin/inventory" element={<HotelAdminInventory />} />
        <Route path="/hoteladmin/add-food" element={<AddFood />} />
      </Routes>
    </>
  );
};

const App = () => (
  <Router>
    <AppWrapper />
  </Router>
);

export default App;
