import React, { useEffect, useState } from "react";
import { Box, Typography } from "@mui/material";
import api from "../../ser/api";

const HotelAdminHome = () => {
  const [orders, setOrders] = useState([]);
  const [profit, setProfit] = useState(0);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await api.get("/hotel/orders"); // backend endpoint: get all orders for this hotel
        setOrders(res.data);
        const totalProfit = res.data.reduce(
          (acc, order) => acc + order.menuItemPrice * order.quantity,
          0
        );
        setProfit(totalProfit);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };
    fetchOrders();
  }, []);

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        Hotel Admin Dashboard
      </Typography>
      <Typography variant="h6" mb={2}>
        Total Profit: â‚¹{profit.toFixed(2)}
      </Typography>
      <Typography variant="body1">
        Total Orders: {orders.length}
      </Typography>
    </Box>
  );
};

export default HotelAdminHome;
