import api from "./api";

export const getAllFoods = () => api.get("/users/restaurants/foods"); // or modify if needed
export const getFoodsByRestaurant = (restaurantId) =>
  api.get(`/users/restaurants/${restaurantId}/foods`);
export const addFood = (food, restaurantId) =>
  api.post(`/hotel/foods?restaurantId=${restaurantId}`, food);
export const updateFood = (food) => api.put("/hotel/foods", food);
export const deleteFood = (foodId) => api.delete(`/hotel/foods/${foodId}`);
