import React from 'react';
import {useState} from 'react';
import api from "../../utils/api"
const AddOrder=({onOrderAdded})=>{
    const[order,setOrder]=useState({
        restaurantName:"",
        cuisineType:"",
        menuItemName:"",
        menuItemDescription:"",
        menuItemPrice:"",
        quantity:"",
        orderStatus:""
    });
    const handleChange=(e)=>{
        setOrder({...order,[e.target.name]:e.target.value});
    }
    const handleSubmit=async (e)=>{
        e.preventDefault();
        if(
            !order.restaurantName||
            !order.cuisineType||
            !order.menuItemDescription||
            !order.menuItemName||
            !order.orderStatus||
            !order.quantity
            )
            {return;}
        try{
            const formattedOrder={
                ...order,
                menuItemPrice: parseFloat(order.menuItemPrice),
                quantity:parseInt(order.quantity)
            }
            const response=await api.post("/orders",formattedOrder)

            onOrderAdded(response.data);
            setOrder({
                restaurantName:"",
                cuisineType:"",
                menuItemName:"",
                menuItemDescription:"",
                menuItemPrice:"",
                quantity:"",
                orderStatus:""
            })

        }
        catch (error){
            console.error("Error submitting order:",error)
        }
    }
    return(
        <form onSubmit={handleSubmit}>
            <input
            name="restaurantName"
            placeholder="Restaurant Name"
            value={order.restaurantName}
            onChange={handleChange}
            />
            <input
            name="cuisineType"
            placeholder="Cuisine Type"
            value={order.cuisineType}
            onChange={handleChange}
            />
            <input
            name="menuItemName"
            placeholder="Menu Item Name"
            value={order.menuItemName}
            onChange={handleChange}
            />
            <input
            name="menuItemDescription"
            placeholder="Item Description"
            value={order.menuItemDescription}
            onChange={handleChange}
            />
            <input
            type="number"
            name="menuItemPrice"
            placeholder="Price"
            value={order.menuItemPrice}
            onChange={handleChange}
            />
            <input
            type="number"
            name="quantity"
            placeholder="Quantity"
            value={order.quantity}
            onChange={handleChange}
            />
            <input
            name="orderStatus"
            placeholder="Order Status"
            value={order.orderStatus}
            onChange={handleChange}
            />
            <button type='submit'>Submit Order</button>
        </form>
    )
}
export default AddOrder;
