import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Card, CardContent, TextField } from "@mui/material";
import { getAllRestaurants, addRestaurant, deleteRestaurant } from "../../services/restaurantService";

function AppAdmin() {
  const username = localStorage.getItem("username") || "Admin";
  const [restaurants, setRestaurants] = useState([]);
  const [newRestaurantName, setNewRestaurantName] = useState("");
  const [newRestaurantAddress, setNewRestaurantAddress] = useState("");
  const [newOwnerName, setNewOwnerName] = useState("");

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    try {
      const res = await getAllRestaurants();
      setRestaurants(res.data);
    } catch (error) {
      console.error("Failed to fetch restaurants:", error);
    }
  };

  const handleAdd = async () => {
    if (!newRestaurantName || !newRestaurantAddress || !newOwnerName) {
      alert("Please fill all fields");
      return;
    }

    try {
      await addRestaurant({
        name: newRestaurantName,
        address: newRestaurantAddress,
        ownerName: newOwnerName,
      });
      setNewRestaurantName("");
      setNewRestaurantAddress("");
      setNewOwnerName("");
      fetchRestaurants();
    } catch (error) {
      console.error("Failed to add restaurant:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteRestaurant(id);
      fetchRestaurants();
    } catch (error) {
      console.error("Failed to delete restaurant:", error);
    }
  };

  return (
    <Box padding={3}>
      <Typography variant="h4" gutterBottom>
        App Admin Dashboard
      </Typography>
      <Typography mb={2}>Manage restaurants and view feedback here.</Typography>
      <Typography variant="body1">Hello, {username}</Typography>

      <Box mt={3} display="flex" gap={1} flexWrap="wrap">
        <TextField
          label="Restaurant Name"
          value={newRestaurantName}
          onChange={(e) => setNewRestaurantName(e.target.value)}
          required
        />
        <TextField
          label="Address"
          value={newRestaurantAddress}
          onChange={(e) => setNewRestaurantAddress(e.target.value)}
          required
        />
        <TextField
          label="Owner Name"
          value={newOwnerName}
          onChange={(e) => setNewOwnerName(e.target.value)}
          required
        />
        <Button onClick={handleAdd} variant="contained" sx={{ height: "fit-content" }}>
          Add Restaurant
        </Button>
      </Box>

      <Grid container spacing={2} mt={3}>
        {restaurants.map((rest) => (
          <Grid item xs={12} sm={6} md={4} key={rest.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{rest.name}</Typography>
                <Typography variant="body2">{rest.address}</Typography>
                <Typography variant="body2">Owner: {rest.ownerName}</Typography>
                <Button
                  color="error"
                  variant="outlined"
                  onClick={() => handleDelete(rest.id)}
                  sx={{ mt: 1 }}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default AppAdmin;
