import React, { useState } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Paper,
  Alert,
  Box,
} from "@mui/material";
import axios from "axios";

const AddRestaurant = () => {
  const [restaurant, setRestaurant] = useState({
    name: "",
    address: "",
    contactEmail: "",
    phoneNumber: "",
  });
  const [error, setError] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  const handleChange = (e) => {
    setRestaurant({ ...restaurant, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccessMsg("");

    try {
      const response = await axios.post(
        "http://localhost:9000/api/restaurants/add",
        restaurant
      );
      setSuccessMsg("Restaurant added successfully!");
      setRestaurant({ name: "", address: "", contactEmail: "", phoneNumber: "" });
    } catch (err) {
      setError(err.response?.data || "Failed to add restaurant");
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <Paper sx={{ p: 4 }}>
        <Typography variant="h5" mb={3}>
          Add New Restaurant
        </Typography>

        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        {successMsg && <Alert severity="success" sx={{ mb: 2 }}>{successMsg}</Alert>}

        <Box component="form" onSubmit={handleSubmit}>
          <TextField
            label="Restaurant Name"
            name="name"
            value={restaurant.name}
            onChange={handleChange}
            fullWidth
            required
            margin="normal"
          />
          <TextField
            label="Address"
            name="address"
            value={restaurant.address}
            onChange={handleChange}
            fullWidth
            required
            margin="normal"
          />
          <TextField
            label="Contact Email"
            name="contactEmail"
            value={restaurant.contactEmail}
            onChange={handleChange}
            fullWidth
            required
            margin="normal"
            type="email"
          />
          <TextField
            label="Phone Number"
            name="phoneNumber"
            value={restaurant.phoneNumber}
            onChange={handleChange}
            fullWidth
            required
            margin="normal"
            type="tel"
          />

          <Button type="submit" variant="contained" fullWidth sx={{ mt: 3 }}>
            Add Restaurant
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default AddRestaurant;
